/* Defino etiqueta */
#define tag "SSD1306"

const unsigned char logo [];

/* Struct for dht11 sensor */
typedef struct {
	int16_t temperatura;
	int16_t humedad;
}hum_temp;

/* Temperature sensor type and sensor gpio */
static const dht_sensor_type_t sensor_type = DHT_TYPE_DHT11;
static const gpio_num_t dht_gpio = 33;

void xDisplayWrite(void *pvParameter) {

	hum_temp sensor;
	sensor.temperatura = 0;
	sensor.humedad = 0;

	char valortemp[1]={0};
	char valorhum[1]={0};

	while(1){
		if (dht_read_data(sensor_type, dht_gpio, &sensor.humedad, &sensor.temperatura) == ESP_OK){

			SSD1306_DisplayClear();
			SSD1306_GotoXY (10, 10);
			SSD1306_Puts ("Temperatura", &Font_7x10, 1);

			itoa((sensor.temperatura*0.1),valortemp,10);

			SSD1306_GotoXY (10, 20);
			SSD1306_Puts (valortemp, &Font_7x10, 1);

			SSD1306_GotoXY (10, 31);
			SSD1306_Puts ("Humedad", &Font_7x10, 1);

			itoa((sensor.humedad*0.1),valorhum,10);

			SSD1306_GotoXY (10, 41);
			SSD1306_Puts (valorhum, &Font_7x10, 1);

			for(uint8_t i = 0; i < 64 ; i++){
			SSD1306_DrawPixel(110,i,SSD1306_COLOR_WHITE);
			}

			for(uint8_t i = 0; i < 64 ; i++){
			SSD1306_DrawPixel(100,i,SSD1306_COLOR_WHITE);
			}

			for(uint8_t i = 0; i < 64 ; i++){
			SSD1306_DrawPixel(90,i,SSD1306_COLOR_WHITE);
			}

			SSD1306_UpdateScreen();

			vTaskDelay(4000/portTICK_PERIOD_MS);

			SSD1306_DisplayClear();
		    SSD1306_DrawBitmap(0,0,logo, 128, 64, 1);
		    SSD1306_UpdateScreen();

		    vTaskDelay(4000/portTICK_PERIOD_MS);
		}
	}
}

void app_main(void)
{
	SSD1306_DisplayInit();

	SSD1306_DisplayClear();

	xTaskCreate(&xDisplayWrite, "xDisplayWrite", 2048, NULL, 6, NULL);

}



